<template>  <input    :value="modelValue"    @input="editItem()"    :ref="`item-input-${item.id}`"    @input="updateValue($event.target.value)"    @keydown.enter="stopEditing"    @blur="stopEditing"  /></template><script>export default {  props: {    modelValue: {      type: String,    },    item: {      type: Object,    },  },  methods: {    isEditingItem(item) {      this.$emit("is-editing-item", item);    },    editItem() {      this.$emit("update:modelValue", $event.target.value);    },    stopEditing() {      this.$emit("stop-editing");    },  },};</script><style scoped>input {  background: transparent;  border: none;  width: 100%;  font-size: 17px;  color: rgb(255, 251, 0);  line-height: 1.6;}</style>